INSERT INTO movie_friend_schema.hall (hall_id, capacity, name, description) VALUES (3, 12, 'Vip hall', 'Зал на нулевом этаже прекрасно подойдет для тех, кто не любит шумные компании и любит проводить время с комфортом. В автоматически раскладывающиеся диванчики встроены планшеты, чтобы вы могли не отвлекаясь от просмотра заказать напиток или закуски. Наш услужливый персонал выполнит ваш заказ максимально быстро и незаметно.');
